   // create an entity with render assets\n            const entity = assets.statue.resource.instantiateModelEntity({\n                castShadows: true\n            });\n\n            app.root.addChild(entity);\n\n            // clone a small version of the entity\n            const clone = entity.clone();\n            clone.setLocalScale(0.2, 0.2, 0.2);\n            clone.setLocalPosition(-4, 12, 0);\n            app.root.addChild(clone);\n\n            // Create an Entity with a camera component\n            const camera = new pc.Entity();\n            camera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.4, 0.45, 0.5)\n            });\n            camera.translate(0, 7, 24);\n            app.root.addChild(camera);\n\n            // Create an Entity with a omni light component\n            const light = new pc.Entity();\n            light.addComponent(\"light\", {\n                type: \"omni\",\n                color: new pc.Color(1, 1, 1),\n                range: 100,\n                castShadows: true\n            });\n            light.translate(5, 0, 15);\n            app.root.addChild(light);\n\n            app.on(\"update\", function (dt) {\n                if (entity) {\n                    entity.rotate(0, 10 * dt, 0);\n                }\n            });\n        });\n    });\n}","javaScriptFunction":"function example(canvas, deviceType) {\n    const assets = {\n        statue: new pc.Asset(\"statue\", \"container\", {\n            url: \"/static/assets/models/statue.glb\",\n        }),\n    };\n\n    const gfxOptions = {\n        deviceTypes: [deviceType],\n        glslangUrl: \"/static/lib/glslang/glslang.js\",\n        twgslUrl: \"/static/lib/twgsl/twgsl.js\",\n    };\n\n    pc.createGraphicsDevice(canvas, gfxOptions).then((device) => {\n        const createOptions = new pc.AppOptions();\n        createOptions.graphicsDevice = device;\n\n        createOptions.componentSystems = [\n            // @ts-ignore\n            pc.ModelComponentSystem,\n            // @ts-ignore\n            pc.CameraComponentSystem,\n            // @ts-ignore\n            pc.LightComponentSystem,\n        ];\n\n        createOptions.resourceHandlers = [\n            // @ts-ignore\n            pc.TextureHandler,\n            // @ts-ignore\n            pc.ContainerHandler,\n        ];\n\n        const app = new pc.AppBase(canvas);\n        app.init(createOptions);\n\n        // Set the canvas to fill the window and automatically change resolution to be the same as the canvas size\n        app.setCanvasFillMode(pc.FILLMODE_FILL_WINDOW);\n        app.setCanvasResolution(pc.RESOLUTION_AUTO);\n\n        const assetListLoader = new pc.AssetListLoader(\n            Object.values(assets),\n            app.assets\n        );\n        assetListLoader.load(() => {\n            app.start();\n\n            app.scene.ambientLight = new pc.Color(0.2, 0.2, 0.2);\n\n            // create an entity with render assets\n            const entity = assets.statue.resource.instantiateModelEntity({\n                castShadows: true,\n            });\n\n            app.root.addChild(entity);\n\n            // clone a small version of the entity\n            const clone = entity.clone();\n            clone.setLocalScale(0.2, 0.2, 0.2);\n            clone.setLocalPosition(-4, 12, 0);\n            app.root.addChild(clone);\n\n            // Create an Entity with a camera component\n            const camera = new pc.Entity();\n            camera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.4, 0.45, 0.5),\n            });\n\n            camera.translate(0, 7, 24);\n            app.root.addChild(camera);\n\n            // Create an Entity with a omni light component\n            const light = new pc.Entity();\n            light.addComponent(\"light\", {\n                type: \"omni\",\n                color: new pc.Color(1, 1, 1),\n                range: 100,\n                castShadows: true,\n            });\n\n            light.translate(5, 0, 15);\n            app.root.addChild(light);\n\n            app.on(\"update\", function (dt) {\n                if (entity) {\n                    entity.rotate(0, 10 * dt, 0);\n                }\n            });\n        });\n    });\n}\n","nameSlug":"model-asset","categorySlug":"graphics"},"model-outline":{"typeScriptFunction":"function example(canvas: HTMLCanvasElement, deviceType: string): void {\n\n    const assets = {\n        'outline': new pc.Asset('outline', 'script', { url: '/static/scripts/posteffects/posteffect-outline.js' })\n    };\n\n    const gfxOptions = {\n        deviceTypes: [deviceType],\n        glslangUrl: '/static/lib/glslang/glslang.js',\n        twgslUrl: '/static/lib/twgsl/twgsl.js'\n    };\n\n    pc.createGraphicsDevice(canvas, gfxOptions).then((device: pc.GraphicsDevice) => {\n\n        const createOptions = new pc.AppOptions();\n        createOptions.graphicsDevice = device;\n        createOptions.mouse = new pc.Mouse(document.body);\n        createOptions.touch = new pc.TouchDevice(document.body);\n        createOptions.keyboard = new pc.Keyboard(document.body);\n\n        createOptions.componentSystems = [\n            // @ts-ignore\n            pc.RenderComponentSystem,\n            // @ts-ignore\n            pc.CameraComponentSystem,\n            // @ts-ignore\n            pc.LightComponentSystem,\n            // @ts-ignore\n            pc.ScriptComponentSystem\n        ];\n        createOptions.resourceHandlers = [\n            // @ts-ignore\n            pc.ScriptHandler\n        ];\n\n        const app = new pc.AppBase(canvas);\n        app.init(createOptions);\n\n        // Set the canvas to fill the window and automatically change resolution to be the same as the canvas size\n        app.setCanvasFillMode(pc.FILLMODE_FILL_WINDOW);\n        app.setCanvasResolution(pc.RESOLUTION_AUTO);\n\n        const assetListLoader = new pc.AssetListLoader(Object.values(assets), app.assets);\n        assetListLoader.load(() => {\n\n            app.start();\n\n            app.scene.ambientLight = new pc.Color(0.2, 0.2, 0.2);\n\n            // helper function to create a primitive with shape type, position, scale, color and layer\n            function createPrimitive(primitiveType: string, position: number | pc.Vec3, scale: number | pc.Vec3, color: pc.Color, layer: number[]) {\n                // create material of specified color\n                const material = new pc.StandardMaterial();\n                material.diffuse = color;\n                material.update();\n\n                // create primitive\n                const primitive = new pc.Entity();\n                primitive.addComponent('render', {\n                    type: primitiveType,\n                    layers: layer,\n                    material: material\n                });\n\n                // set position and scale and add it to scene\n                primitive.setLocalPosition(position);\n                primitive.setLocalScale(scale);\n                app.root.addChild(primitive);\n\n                return primitive;\n            }\n\n            // create texture and render target for rendering into, including depth buffer\n            function createRenderTarget() {\n                const texture = new pc.Texture(app.graphicsDevice, {\n                    name: 'OutlineObjects',\n                    width: app.graphicsDevice.width,\n                    height: app.graphicsDevice.height,\n                    format: pc.PIXELFORMAT_RGBA8,\n                    mipmaps: false,\n                    minFilter: pc.FILTER_LINEAR,\n                    magFilter: pc.FILTER_LINEAR\n                });\n                return new pc.RenderTarget({\n                    colorBuffer: texture,\n                    depth: true\n                });\n            }\n\n            let renderTarget = createRenderTarget();\n\n            // create a layer for rendering to texture, and add it to the beginning of layers to render into it first\n            const outlineLayer = new pc.Layer({ name: \"OutlineLayer\" });\n            app.scene.layers.insert(outlineLayer, 0);\n\n            // get world layer\n            const worldLayer = app.scene.layers.getLayerByName(\"World\");\n\n            // create ground plane and 3 primitives, visible in both layers\n            createPrimitive(\"plane\", new pc.Vec3(0, 0, 0), new pc.Vec3(20, 20, 20), new pc.Color(0.3, 0.5, 0.3), [worldLayer.id]);\n            createPrimitive(\"sphere\", new pc.Vec3(-2, 1, 0), new pc.Vec3(2, 2, 2), new pc.Color(1, 0, 0), [worldLayer.id]);\n            createPrimitive(\"box\", new pc.Vec3(2, 1, 0), new pc.Vec3(2, 2, 2), new pc.Color(1, 1, 0), [worldLayer.id, outlineLayer.id]);\n            createPrimitive(\"cone\", new pc.Vec3(0, 1, -2), new pc.Vec3(2, 2, 2), new pc.Color(0, 1, 1), [worldLayer.id]);\n\n            // Create main camera, which renders entities in world layer\n            const camera = new pc.Entity();\n            camera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.2, 0.2, 0.4),\n                layers: [worldLayer.id]\n            });\n            camera.translate(0, 20, 25);\n            camera.lookAt(pc.Vec3.ZERO);\n\n            // Create outline camera, which renders entities in outline layer into the render target\n            const outlineCamera = new pc.Entity();\n            outlineCamera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.0, 0.0, 0.0, 0.0),\n                layers: [outlineLayer.id],\n                renderTarget: renderTarget\n            });\n            app.root.addChild(outlineCamera);\n\n            // @ts-ignore engine-tsd\n            const outline = new OutlineEffect(app.graphicsDevice, 3);\n            outline.color = new pc.Color(0, 0.5, 1, 1);\n            outline.texture = renderTarget.colorBuffer;\n            camera.camera.postEffects.addEffect(outline);\n\n            app.root.addChild(camera);\n\n            // Create an Entity with a omni light component and add it to both layers\n            const light = new pc.Entity();\n            light.addComponent(\"light\", {\n                type: \"omni\",\n                color: new pc.Color(1, 1, 1),\n                range: 20,\n                castShadows: true,\n                shadowBias: 0.05,\n                normalOffsetBias: 0.03,\n                layers: [worldLayer.id]\n            });\n            light.translate(0, 2, 5);\n            app.root.addChild(light);\n\n            // handle canvas resize\n            window.addEventListener(\"resize\", function () {\n                app.resizeCanvas(canvas.width, canvas.height);\n\n                // re-create the render target for the outline camera\n                renderTarget.colorBuffer.destroy();\n                renderTarget.destroy();\n                renderTarget = createRenderTarget();\n                outlineCamera.camera.renderTarget = renderTarget;\n                outline.texture = renderTarget.colorBuffer;\n            });\n\n            // update things each frame\n            let time = 0;\n            app.on(\"update\", function (dt) {\n                time += dt;\n\n                // rotate the camera around the objects\n                camera.setLocalPosition(12 * Math.sin(time), 5, 12 * Math.cos(time));\n                camera.lookAt(pc.Vec3.ZERO);\n\n                // outline camera needs to match the main camera\n                outlineCamera.setLocalPosition(camera.getLocalPosition());\n                outlineCamera.setLocalRotation(camera.getLocalRotation());\n            });\n        });\n    });\n}","javaScriptFunction":"function example(canvas, deviceType) {\n    const assets = {\n        outline: new pc.Asset(\"outline\", \"script\", {\n            url: \"/static/scripts/posteffects/posteffect-outline.js\",\n        }),\n    };\n\n    const gfxOptions = {\n        deviceTypes: [deviceType],\n        glslangUrl: \"/static/lib/glslang/glslang.js\",\n        twgslUrl: \"/static/lib/twgsl/twgsl.js\",\n    };\n\n    pc.createGraphicsDevice(canvas, gfxOptions).then((device) => {\n        const createOptions = new pc.AppOptions();\n        createOptions.graphicsDevice = device;\n        createOptions.mouse = new pc.Mouse(document.body);\n        createOptions.touch = new pc.TouchDevice(document.body);\n        createOptions.keyboard = new pc.Keyboard(document.body);\n\n        createOptions.componentSystems = [\n            // @ts-ignore\n            pc.RenderComponentSystem,\n            // @ts-ignore\n            pc.CameraComponentSystem,\n            // @ts-ignore\n            pc.LightComponentSystem,\n            // @ts-ignore\n            pc.ScriptComponentSystem,\n        ];\n\n        createOptions.resourceHandlers = [\n            // @ts-ignore\n            pc.ScriptHandler,\n        ];\n\n        const app = new pc.AppBase(canvas);\n        app.init(createOptions);\n\n        // Set the canvas to fill the window and automatically change resolution to be the same as the canvas size\n        app.setCanvasFillMode(pc.FILLMODE_FILL_WINDOW);\n        app.setCanvasResolution(pc.RESOLUTION_AUTO);\n\n        const assetListLoader = new pc.AssetListLoader(\n            Object.values(assets),\n            app.assets\n        );\n        assetListLoader.load(() => {\n            app.start();\n\n            app.scene.ambientLight = new pc.Color(0.2, 0.2, 0.2);\n\n            // helper function to create a primitive with shape type, position, scale, color and layer\n            function createPrimitive(\n                primitiveType,\n                position,\n                scale,\n                color,\n                layer\n            ) {\n                // create material of specified color\n                const material = new pc.StandardMaterial();\n                material.diffuse = color;\n                material.update();\n\n                // create primitive\n                const primitive = new pc.Entity();\n                primitive.addComponent(\"render\", {\n                    type: primitiveType,\n                    layers: layer,\n                    material: material,\n                });\n\n                // set position and scale and add it to scene\n                primitive.setLocalPosition(position);\n                primitive.setLocalScale(scale);\n                app.root.addChild(primitive);\n\n                return primitive;\n            }\n\n            // create texture and render target for rendering into, including depth buffer\n            function createRenderTarget() {\n                const texture = new pc.Texture(app.graphicsDevice, {\n                    name: \"OutlineObjects\",\n                    width: app.graphicsDevice.width,\n                    height: app.graphicsDevice.height,\n                    format: pc.PIXELFORMAT_RGBA8,\n                    mipmaps: false,\n                    minFilter: pc.FILTER_LINEAR,\n                    magFilter: pc.FILTER_LINEAR,\n                });\n\n                return new pc.RenderTarget({\n                    colorBuffer: texture,\n                    depth: true,\n                });\n            }\n\n            let renderTarget = createRenderTarget();\n\n            // create a layer for rendering to texture, and add it to the beginning of layers to render into it first\n            const outlineLayer = new pc.Layer({ name: \"OutlineLayer\" });\n            app.scene.layers.insert(outlineLayer, 0);\n\n            // get world layer\n            const worldLayer = app.scene.layers.getLayerByName(\"World\");\n\n            // create ground plane and 3 primitives, visible in both layers\n            createPrimitive(\n                \"plane\",\n                new pc.Vec3(0, 0, 0),\n                new pc.Vec3(20, 20, 20),\n                new pc.Color(0.3, 0.5, 0.3),\n                [worldLayer.id]\n            );\n            createPrimitive(\n                \"sphere\",\n                new pc.Vec3(-2, 1, 0),\n                new pc.Vec3(2, 2, 2),\n                new pc.Color(1, 0, 0),\n                [worldLayer.id]\n            );\n            createPrimitive(\n                \"box\",\n                new pc.Vec3(2, 1, 0),\n                new pc.Vec3(2, 2, 2),\n                new pc.Color(1, 1, 0),\n                [worldLayer.id, outlineLayer.id]\n            );\n            createPrimitive(\n                \"cone\",\n                new pc.Vec3(0, 1, -2),\n                new pc.Vec3(2, 2, 2),\n                new pc.Color(0, 1, 1),\n                [worldLayer.id]\n            );\n\n            // Create main camera, which renders entities in world layer\n            const camera = new pc.Entity();\n            camera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.2, 0.2, 0.4),\n                layers: [worldLayer.id],\n            });\n\n            camera.translate(0, 20, 25);\n            camera.lookAt(pc.Vec3.ZERO);\n\n            // Create outline camera, which renders entities in outline layer into the render target\n            const outlineCamera = new pc.Entity();\n            outlineCamera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.0, 0.0, 0.0, 0.0),\n                layers: [outlineLayer.id],\n                renderTarget: renderTarget,\n            });\n\n            app.root.addChild(outlineCamera);\n\n            // @ts-ignore engine-tsd\n            const outline = new OutlineEffect(app.graphicsDevice, 3);\n            outline.color = new pc.Color(0, 0.5, 1, 1);\n            outline.texture = renderTarget.colorBuffer;\n            camera.camera.postEffects.addEffect(outline);\n\n            app.root.addChild(camera);\n\n            // Create an Entity with a omni light component and add it to both layers\n            const light = new pc.Entity();\n            light.addComponent(\"light\", {\n                type: \"omni\",\n                color: new pc.Color(1, 1, 1),\n                range: 20,\n                castShadows: true,\n                shadowBias: 0.05,\n                normalOffsetBias: 0.03,\n                layers: [worldLayer.id],\n            });\n\n            light.translate(0, 2, 5);\n            app.root.addChild(light);\n\n            // handle canvas resize\n            window.addEventListener(\"resize\", function () {\n                app.resizeCanvas(canvas.width, canvas.height);\n\n                // re-create the render target for the outline camera\n                renderTarget.colorBuffer.destroy();\n                renderTarget.destroy();\n                renderTarget = createRenderTarget();\n                outlineCamera.camera.renderTarget = renderTarget;\n                outline.texture = renderTarget.colorBuffer;\n            });\n\n            // update things each frame\n            let time = 0;\n            app.on(\"update\", function (dt) {\n                time += dt;\n\n                // rotate the camera around the objects\n                camera.setLocalPosition(\n                    12 * Math.sin(time),\n                    5,\n                    12 * Math.cos(time)\n                );\n                camera.lookAt(pc.Vec3.ZERO);\n\n                // outline camera needs to match the main camera\n                outlineCamera.setLocalPosition(camera.getLocalPosition());\n                outlineCamera.setLocalRotation(camera.getLocalRotation());\n            });\n        });\n    });\n}\n","nameSlug":"model-outline","categorySlug":"graphics"},"model-textured-box":{"typeScriptFunction":"function example(canvas: HTMLCanvasElement, deviceType: string): void {\n\n    const assets = {\n        'clouds': new pc.Asset('clouds', 'texture', { url: '/static/assets/textures/clouds.jpg' })\n    };\n\n    const gfxOptions = {\n        deviceTypes: [deviceType],\n        glslangUrl: '/static/lib/glslang/glslang.js',\n        twgslUrl: '/static/lib/twgsl/twgsl.js'\n    };\n\n    pc.createGraphicsDevice(canvas, gfxOptions).then((device: pc.GraphicsDevice) => {\n\n        const createOptions = new pc.AppOptions();\n        createOptions.graphicsDevice = device;\n\n        createOptions.componentSystems = [\n            // @ts-ignore\n            pc.RenderComponentSystem,\n            // @ts-ignore\n            pc.CameraComponentSystem,\n            // @ts-ignore\n            pc.LightComponentSystem\n        ];\n        createOptions.resourceHandlers = [\n            // @ts-ignore\n            pc.TextureHandler\n        ];\n\n        const app = new pc.AppBase(canvas);\n        app.init(createOptions);\n\n        // Set the canvas to fill the window and automatically change resolution to be the same as the canvas size\n        app.setCanvasFillMode(pc.FILLMODE_FILL_WINDOW);\n        app.setCanvasResolution(pc.RESOLUTION_AUTO);\n\n        const assetListLoader = new pc.AssetListLoader(Object.values(assets), app.assets);\n        assetListLoader.load(() => {\n\n            app.start();\n\n            app.scene.ambientLight = new pc.Color(0.2, 0.2, 0.2);\n\n            // material with the diffuse texture\n            const material = new pc.StandardMaterial();\n            material.diffuseMap = assets.clouds.resource;\n            material.update();\n\n            // Create a Entity with a Box model component\n            const box = new pc.Entity();\n            box.addComponent(\"render\", {\n                type: \"box\",\n                material: material\n            });\n\n            // Create an Entity with a omni light component and a sphere model component.\n            const light = new pc.Entity();\n            light.addComponent(\"light\", {\n                type: \"omni\",\n                color: new pc.Color(1, 0, 0),\n                radius: 10\n            });\n            light.addComponent(\"render\", {\n                type: \"sphere\"\n            });\n            // Scale the sphere down to 0.1m\n            light.setLocalScale(0.1, 0.1, 0.1);\n\n            // Create an Entity with a camera component\n            const camera = new pc.Entity();\n            camera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.4, 0.45, 0.5)\n            });\n\n            // Add the new Entities to the hierarchy\n            app.root.addChild(box);\n            app.root.addChild(light);\n            app.root.addChild(camera);\n\n            // Move the camera 10m along the z-axis\n            camera.translate(0, 0, 10);\n\n            // Set an update function on the app's update event\n            let angle = 0;\n            app.on(\"update\", function (dt) {\n                angle += dt;\n                if (angle > 360) {\n                    angle = 0;\n                }\n\n                // Move the light in a circle\n                light.setLocalPosition(3 * Math.sin(angle), 0, 3 * Math.cos(angle));\n\n                // Rotate the box\n                box.setEulerAngles(angle * 2, angle * 4, angle * 8);\n            });\n        });\n    });\n}","javaScriptFunction":"function example(canvas, deviceType) {\n    const assets = {\n        clouds: new pc.Asset(\"clouds\", \"texture\", {\n            url: \"/static/assets/textures/clouds.jpg\",\n        }),\n    };\n\n    const gfxOptions = {\n        deviceTypes: [deviceType],\n        glslangUrl: \"/static/lib/glslang/glslang.js\",\n        twgslUrl: \"/static/lib/twgsl/twgsl.js\",\n    };\n\n    pc.createGraphicsDevice(canvas, gfxOptions).then((device) => {\n        const createOptions = new pc.AppOptions();\n        createOptions.graphicsDevice = device;\n\n        createOptions.componentSystems = [\n            // @ts-ignore\n            pc.RenderComponentSystem,\n            // @ts-ignore\n            pc.CameraComponentSystem,\n            // @ts-ignore\n            pc.LightComponentSystem,\n        ];\n\n        createOptions.resourceHandlers = [\n            // @ts-ignore\n            pc.TextureHandler,\n        ];\n\n        const app = new pc.AppBase(canvas);\n        app.init(createOptions);\n\n        // Set the canvas to fill the window and automatically change resolution to be the same as the canvas size\n        app.setCanvasFillMode(pc.FILLMODE_FILL_WINDOW);\n        app.setCanvasResolution(pc.RESOLUTION_AUTO);\n\n        const assetListLoader = new pc.AssetListLoader(\n            Object.values(assets),\n            app.assets\n        );\n        assetListLoader.load(() => {\n            app.start();\n\n            app.scene.ambientLight = new pc.Color(0.2, 0.2, 0.2);\n\n            // material with the diffuse texture\n            const material = new pc.StandardMaterial();\n            material.diffuseMap = assets.clouds.resource;\n            material.update();\n\n            // Create a Entity with a Box model component\n            const box = new pc.Entity();\n            box.addComponent(\"render\", {\n                type: \"box\",\n                material: material,\n            });\n\n            // Create an Entity with a omni light component and a sphere model component.\n            const light = new pc.Entity();\n            light.addComponent(\"light\", {\n                type: \"omni\",\n                color: new pc.Color(1, 0, 0),\n                radius: 10,\n            });\n\n            light.addComponent(\"render\", {\n                type: \"sphere\",\n            });\n\n            // Scale the sphere down to 0.1m\n            light.setLocalScale(0.1, 0.1, 0.1);\n\n            // Create an Entity with a camera component\n            const camera = new pc.Entity();\n            camera.addComponent(\"camera\", {\n                clearColor: new pc.Color(0.4, 0.45, 0.5),\n            });\n\n            // Add the new Entities to the hierarchy\n            app.root.addChild(box);\n            app.root.addChild(light);\n            app.root.addChild(camera);\n\n            // Move the camera 10m along the z-axis\n            camera.translate(0, 0, 10);\n\n            // Set an update function on the app's update event\n            let angle = 0;\n            app.on(\"update\", function (dt) {\n                angle += dt;\n                if (angle > 360) {\n                    angle = 0;\n                }\n\n                // Move the light in a circle\n                light.setLocalPosition(\n                    3 * Math.sin(angle),\n                    0,\n                    3 * Math.cos(angle)\n                );\n\n                // Rotate the box\n                box.setEulerAngles(angle * 2, angle * 4, angle * 8);\n            });\n        });\n    });\n}\n","nameSlug":"model-textured-box","categorySlug":"graphics"},"mrt":{"typeScriptFunction":"function example(canvas: HTMLCanvasElement, deviceType: string, files: { 'output.frag': string, }): void {\n\n    // set up and load draco module, as the glb we load is draco compressed\n    pc.WasmModule.setConfig('DracoDecoderModule', {\n        glueUrl: '/static/lib/draco/draco.wasm.js',\n        wasmUrl: '/static/lib/draco/draco.wasm.wasm',\n        fallbackUrl: '/static/lib/draco/draco.js'\n    });\n\n    const assets = {\n        'board': new pc.Asset('statue', 'container', { url: '/static/assets/models/chess-board.glb' }),\n        helipad: new pc.Asset('helipad-env-atlas', 'texture', { url: '/static/assets/cubemaps/helipad-env-atlas.png' }, { type: pc.TEXTURETYPE_RGBP, mipmaps: false })\n    };\n\n    const gfxOptions = {\n        deviceTypes: [deviceType],\n        glslangUrl: '/static/lib/glslang/glslang.js',\n        twgslUrl: '/static/lib/twgsl/twgsl.js'\n    };\n\n    pc.createGraphicsDevice(canvas, gfxOptions).then((device: pc.GraphicsDevice) => {\n\n        const createOptions = new pc.AppOptions();\n        createOptions.graphicsDevice = device;\n        createOptions.keyboard = new pc.Keyboard(document.body);\n\n        createOptions.componentSystems = [\n            // @ts-ignore\n            pc.RenderComponentSystem,\n            // @ts-ignore\n            pc.CameraComponentSystem,\n            // @ts-ignore\n            pc.LightComponentSystem,\n            // @ts-ignore\n            pc.ScriptComponentSystem,\n            // @ts-ignore\n            pc.ScreenComponentSystem,\n            // @ts-ignore\n            pc.ElementComponentSystem\n        ];\n        createOptions.resourceHandlers = [\n            // @ts-ignore\n            pc.ScriptHandler,\n            // @ts-ignore\n            pc.TextureHandler,\n            // @ts-ignore\n            pc.ContainerHandler,\n            // @ts-ignore\n            pc.FontHandler\n        ];\n\n        const app = new pc.AppBase(canvas);\n        app.init(createOptions);\n\n        // Set the canvas to fill the window and automatically change resolution to be the same as the canvas size\n        app.setCanvasFillMode(pc.FILLMODE_FILL_WINDOW);\n        app.setCanvasResolution(pc.RESOLUTION_AUTO);\n\n        const assetListLoader = new pc.AssetListLoader(Object.values(assets), app.assets);\n        assetListLoader.load(() => {\n\n            app.start();\n\n            // setup skydome\n            app.scene.envAtlas = assets.helipad.resource;\n            app.scene.skyboxMip = 1;\n            app.scene.toneMapping = pc.TONEMAP_ACES;\n\n\n            // get world and skybox layers\n            const worldLayer = app.scene.layers.getLayerByName(\"World\");\n            const skyboxLayer = app.scene.layers.getLayerByName(\"Skybox\");\n\n            // create a layer for object that render into texture, add it right after the world layer\n            const rtLayer = new pc.Layer({ name: \"RTLayer\" });\n            app.scene.layers.insert(rtLayer, 1);\n\n            // helper function to create a texture to render to\n            const createTexture = (name: string, width: number, height: number) => {\n                return new pc.Texture(app.graphicsDevice, {\n                    name: name,\n                    width: width,\n                    height: height,\n                    format: pc.PIXELFORMAT_R8_G8_B8_A8,\n                    mipmaps: true,\n                    minFilter: pc.FILTER_LINEAR_MIPMAP_LINEAR,\n                    magFilter: pc.FILTER_LINEAR,\n                    addressU: pc.ADDRESS_CLAMP_TO_EDGE,\n                    addressV: pc.ADDRESS_CLAMP_TO_EDGE\n                });\n            };\n\n            // create textures and render target for rendering into, including depth buffer\n            const texture0 = createTexture('RT-texture-0', 512, 512);\n            const texture1 = createTexture('RT-texture-1', 512, 512);\n            const texture2 = createTexture('RT-texture-2', 512, 512);\n\n            // render to multiple targets if supported\n            const colorBuffers = app.graphicsDevice.supportsMrt ? [texture0, texture1, texture2] : [texture0];\n            const renderTarget = new pc.RenderTarget({\n                name: `MRT`,\n                colorBuffers: colorBuffers,\n                depth: true,\n                flipY: true,\n                samples: 2\n            });\n\n            // Create texture camera, which renders entities in RTLayer into the texture\n            const textureCamera = new pc.Entity(\"TextureCamera\");\n            textureCamera.addComponent(\"camera\", {\n                layers: [rtLayer.id],\n                farClip: 500,\n\n                // set the priority of textureCamera to lower number than the priority of the main camera (which is at default 0)\n                // to make it rendered first each frame\n                priority: -1,\n\n                // this camera renders into texture target\n                renderTarget: renderTarget\n            });\n            app.root.addChild(textureCamera);\n\n            // if MRT is supported, set the shader pass to use MRT output\n            if (app.graphicsDevice.supportsMrt) {\n                textureCamera.camera.setShaderPass('MyMRT');\n            }\n\n            // get the instance o